mapper.insert:
insert into cart(
	cartNo
	, userNo
	, productNo
	, productCount
	, productSize
)values(
	seq_cart_no.nextval
	, #{userNo}
	, #{productNo}
	, #{productCount}
	, #{productSize})


	insert into user_order(
		orderNo
		, userNo
		, orderDate
		, postnum
		, address1
		, address2
		, receiverFirstName
		, receiverLastName
		, receiverPhone
	)values(
		seq_order_no.nextval
		, #{userNo}
		, sysdate
		, #{postNum}
		, #{addr1}
		, #{addr2}
		, #{reFirstName}
		, #{reLastName}
		, #{rePhone})


       SELECT MAX(orderNo) FROM user_order

mapper.delete:
delete cart
    where cartNo = #{cartNo}
        and userNo = #{userNo}
